jQuery(document).ready(function($){$("#scroller").mousewheel(function(l,i){this.scrollLeft-=2*i,l.preventDefault()}),$("#scroller img").click(function(){var l=$(this).width()/2,i=$(window).width()/2,r=i-l;$("#scroller").scrollTo(this,600,{offset:function(){return{left:-r}}})});var l=0;$("#scroller").scroll(function(){var i=$("#scroller").scrollLeft();if(l!==i){var r=Math.floor(window.innerWidth/2);$("#scroller li").each(function(l){var i=$(this).position(),s=i.left;if($(this).hasClass("active"));else{var t=$(this).width();r>s&&s+t>r&&($("#scroller li").removeClass("active"),$("#scroller li p.desc").stop().animate({opacity:0}),$(this).addClass("active"),$(this).children("p.desc").stop().animate({opacity:1}))}}),l=i}})}),function($){$(window).load(function(){var l=$(window).width()/2,i=$("#scroller li img").first().width()/2,r=l-i-200;$("#scroller ul").css("padding-left",r);var s=$("#scroller li img").last().width()/2,t=l-s;$("#scroller li").last().css("margin-right",t),$("#scroller").scrollTo(r,0)})}(jQuery);